  StepContent,
  Button,
  CircularProgress,
  Alert,
  Grid,
  Chip,
  IconButton,
  Tooltip,
  Accordion,
  AccordionSummary,
  AccordionDetails
} from '@mui/material';
import {
  ExpandMore,
  Lightbulb,
  School,
  CheckCircle,
  BookmarkBorder,
  Bookmark,
  VolumeUp,
  ThumbUp,
  ThumbDown,
  Help
} from '@mui/icons-material';

function QuestionSolver() {
  const { id } = useParams();
  const [question, setQuestion] = useState(null);
  const [solution, setSolution] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeStep, setActiveStep] = useState(0);
  const [bookmarked, setBookmarked] = useState(false);
  const [feedback, setFeedback] = useState(null);
  const [relatedConcepts, setRelatedConcepts] = useState([]);
  const [showHint, setShowHint] = useState(false);
  
  useEffect(() => {
    const fetchQuestionAndSolution = async () => {
      setLoading(true);
      setError('');
      
      try {
        // 获取题目信息
        const questionResponse = await fetch(`/api/questions/${id}`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        
        // 获取解答信息
        const solutionResponse = await fetch(`/api/solutions/${id}`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        
        // 获取相关知识点
        const conceptsResponse = await fetch(`/api/concepts/related/${id}`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        
        if (questionResponse.ok && solutionResponse.ok && conceptsResponse.ok) {
          const questionData = await questionResponse.json();
          const solutionData = await solutionResponse.json();
          const conceptsData = await conceptsResponse.json();
          
          setQuestion(questionData);
          setSolution(solutionData);
          setRelatedConcepts(conceptsData);
          setBookmarked(questionData.bookmarked || false);
          setFeedback(questionData.feedback);
        } else {
          throw new Error('获取题目或解答失败');
        }
      } catch (err) {
        console.error('获取数据失败:', err);
        setError('获取题目或解答失败，请重试');
      } finally {
        setLoading(false);
      }
    };
    
    fetchQuestionAndSolution();
  }, [id]);
  
  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
    // 记录学习进度
    updateLearningProgress(activeStep + 1);
  };
  
  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };
  
  const handleReset = () => {
    setActiveStep(0);
  };
  
  const toggleBookmark = async () => {
    try {
      const response = await fetch(`/api/questions/${id}/bookmark`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ bookmarked: !bookmarked })
      });
      
      if (response.ok) {
        setBookmarked(!bookmarked);
      }
    } catch (err) {
      console.error('书签操作失败:', err);
    }
  };
  
  const provideFeedback = async (isHelpful) => {
    try {
      const response = await fetch(`/api/solutions/${id}/feedback`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ helpful: isHelpful })
      });
      
      if (response.ok) {
        setFeedback(isHelpful ? 'helpful' : 'not_helpful');
      }
    } catch (err) {
      console.error('反馈提交失败:', err);
    }
  };
  
  const updateLearningProgress = async (step) => {
    if (!solution || !solution.steps) return;
    
    const progress = Math.round((step / solution.steps.length) * 100);
    
    try {
      await fetch('/api/progress/update', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          questionId: id,
          conceptIds: relatedConcepts.map(concept => concept.id),
          progress: progress
        })
      });
    } catch (err) {
      console.error('更新学习进度失败:', err);
    }
  };
  
  const speakText = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = 'zh-CN';
      window.speechSynthesis.speak(utterance);
    }
  };
  
  if (loading) {
    return (
      <Container maxWidth="md" sx={{ mt: 4, textAlign: 'center' }}>
        <CircularProgress />
        <Typography variant="body1" sx={{ mt: 2 }}>
          正在生成解答，请稍候...
        </Typography>
      </Container>
    );
  }
  
  if (error) {
    return (
      <Container maxWidth="md" sx={{ mt: 4 }}>
        <Alert severity="error">{error}</Alert>
      </Container>
    );
  }
  
  if (!question || !solution) {
    return (
      <Container maxWidth="md" sx={{ mt: 4 }}>
        <Alert severity="warning">未找到题目或解答信息</Alert>
      </Container>
    );
  }
  
  return (
    <Container maxWidth="md">
      <Paper elevation={3} sx={{ p: 4, mt: 4 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 3 }}>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <School color="primary" sx={{ fontSize: 40, mr: 2 }} />
            <Typography variant="h4">
              题目解答
            </Typography>
          </Box>
          
          <IconButton onClick={toggleBookmark} color={bookmarked ? 'primary' : 'default'}>
            {bookmarked ? <Bookmark /> : <BookmarkBorder />}
          </IconButton>
        </Box>
        
        <Card variant="outlined" sx={{ mb: 4 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              题目内容
            </Typography>
            <Typography variant="body1" paragraph>
              {question.content}
            </Typography>
            
            <Box sx={{ display: 'flex', alignItems: 'center', mt: 2 }}>
              <Typography variant="body2" color="text.secondary">
                上传时间: {new Date(question.createdAt).toLocaleString()}
              </Typography>
              <Chip 
                size="small" 
                label={question.type === 'image' ? '图片上传' : (question.type === 'voice' ? '语音输入' : '文字输入')} 
                sx={{ ml: 2 }} 
              />
            </Box>
          </CardContent>
        </Card>
        
        <Typography variant="h5" gutterBottom>
          解答过程
        </Typography>
        
        <Box sx={{ mb: 3 }}>
          <Button
            variant="outlined"
            startIcon={<Lightbulb />}
            onClick={() => setShowHint(!showHint)}
            sx={{ mb: 2 }}
          >
            {showHint ? '隐藏提示' : '显示提示'}
          </Button>
          
          {showHint && (
            <Alert severity="info" sx={{ mb: 2 }}>
              <Typography variant="body1">
                {solution.hint || '解题提示：仔细观察题目条件，思考可能用到的知识点。'}
              </Typography>
            </Alert>
          )}
        </Box>
        
        <Stepper activeStep={activeStep} orientation="vertical">
          {solution.steps.map((step, index) => (
            <Step key={index}>
              <StepLabel>
                <Typography variant="subtitle1">
                  {step.title}
                </Typography>
              </StepLabel>
              <StepContent>
                <Typography variant="body1" paragraph>
                  {step.content}
                </Typography>
                
                {step.formula && (
                  <Box sx={{ bgcolor: '#f5f5f5', p: 2, borderRadius: 1, my: 2 }}>
                    <Typography variant="body1" sx={{ fontFamily: 'monospace' }}>
                      {step.formula}
                    </Typography>
                  </Box>
                )}
                
                <Box sx={{ mb: 2 }}>
                  <Tooltip title="朗读此步骤">
                    <IconButton onClick={() => speakText(step.content)}>
                      <VolumeUp />
                    </IconButton>
                  </Tooltip>
                </Box>
                
                <Box sx={{ mb: 2 }}>
                  <Button
                    variant="contained"
                    onClick={handleNext}
                    sx={{ mt: 1, mr: 1 }}
                    disabled={index === solution.steps.length - 1}
                  >
                    {index === solution.steps.length - 1 ? '完成' : '下一步'}
                  </Button>
                  <Button
                    disabled={index === 0}
                    onClick={handleBack}
                    sx={{ mt: 1, mr: 1 }}
                  >
                    上一步
                  </Button>
                </Box>
              </StepContent>
            </Step>
          ))}
        </Stepper>
        
        {activeStep === solution.steps.length && (
          <Paper square elevation={0} sx={{ p: 3, bgcolor: '#f5f5f5', mt: 2 }}>
            <Typography variant="h6" gutterBottom>
              <CheckCircle color="success" sx={{ mr: 1, verticalAlign: 'middle' }} />
              解答完成！
            </Typography>
            <Typography variant="body1" paragraph>
              您已完成本题的全部解答步骤。希望这个解答对您有所帮助！
            </Typography>
            <Button onClick={handleReset} sx={{ mt: 1, mr: 1 }}>
              重新查看解答
            </Button>
            
            <Box sx={{ mt: 3 }}>
              <Typography variant="subtitle1" gutterBottom>
                这个解答对您有帮助吗？
              </Typography>
              <Box sx={{ display: 'flex', gap: 1 }}>
                <Button
                  variant={feedback === 'helpful' ? 'contained' : 'outlined'}
                  color="primary"
                  startIcon={<ThumbUp />}
                  onClick={() => provideFeedback(true)}
                  disabled={feedback !== null}
                >
                  有帮助
                </Button>
                <Button
                  variant={feedback === 'not_helpful' ? 'contained' : 'outlined'}
                  color="error"
                  startIcon={<ThumbDown />}
                  onClick={() => provideFeedback(false)}
                  disabled={feedback !== null}
                >
                  没帮助
                </Button>
              </Box>
            </Box>
          </Paper>
        )}
        
        <Divider sx={{ my: 4 }} />
        
        <Typography variant="h5" gutterBottom>
          相关知识点
        </Typography>
        
        <Grid container spacing={2} sx={{ mb: 3 }}>
          {relatedConcepts.map((concept) => (
            <Grid item xs={12} key={concept.id}>
              <Accordion>
                <AccordionSummary expandIcon={<ExpandMore />}>
                  <Typography variant="subtitle1">{concept.name}</Typography>
                </AccordionSummary>
                <AccordionDetails>
                  <Typography variant="body1" paragraph>
                    {concept.description}
                  </Typography>
                  {concept.examples && (
                    <Box sx={{ mt: 2 }}>
                      <Typography variant="subtitle2" gutterBottom>
                        示例:
                      </Typography>
                      <Typography variant="body2">
                        {concept.examples}
                      </Typography>
                    </Box>
                  )}
                </AccordionDetails>
              </Accordion>
            </Grid>
          ))}
        </Grid>
      </Paper>
    </Container>
  );
}

export default QuestionSolver;
