import React, { useState } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Button,
  Grid,
} from '@mui/material';

const TestGenerator = () => {
  const [testConfig, setTestConfig] = useState({
    type: '',
    difficulty: '',
    questionCount: 10,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTestConfig((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: 实现测试生成逻辑
    console.log('测试配置：', testConfig);
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} sx={{ p: 4, mt: 4 }}>
        <Typography variant="h4" align="center" gutterBottom>
          生成测试
        </Typography>
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 3 }}>
          <Grid container spacing={3}>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth>
                <InputLabel>测试类型</InputLabel>
                <Select
                  name="type"
                  value={testConfig.type}
                  label="测试类型"
                  onChange={handleChange}
                  required
                >
                  <MenuItem value="calculus">微积分</MenuItem>
                  <MenuItem value="algebra">线性代数</MenuItem>
                  <MenuItem value="probability">概率统计</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth>
                <InputLabel>难度等级</InputLabel>
                <Select
                  name="difficulty"
                  value={testConfig.difficulty}
                  label="难度等级"
                  onChange={handleChange}
                  required
                >
                  <MenuItem value="easy">简单</MenuItem>
                  <MenuItem value="medium">中等</MenuItem>
                  <MenuItem value="hard">困难</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="题目数量"
                name="questionCount"
                type="number"
                value={testConfig.questionCount}
                onChange={handleChange}
                InputProps={{ inputProps: { min: 1, max: 50 } }}
                required
              />
            </Grid>
            <Grid item xs={12}>
              <Button
                type="submit"
                fullWidth
                variant="contained"
                size="large"
                sx={{ mt: 2 }}
              >
                生成测试
              </Button>
            </Grid>
          </Grid>
        </Box>
      </Paper>
    </Container>
  );
};

export default TestGenerator;
