import React, { useState, useEffect } from 'react';
import {
  Container, Paper, Typography, Box, Grid, Card,
  CardContent, Button, LinearProgress, Chip,
  Dialog, DialogTitle, DialogContent, DialogActions,
  Stepper, Step, StepLabel, StepContent
} from '@mui/material';
import {
  PlayArrow, CheckCircle, Lock, Star,
  EmojiEvents, Timeline
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';

function QuestChain() {
  const [quests, setQuests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedQuest, setSelectedQuest] = useState(null);
  const [dialogOpen, setDialogOpen] = useState(false);
  const [completionStatus, setCompletionStatus] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchQuests();
    checkCompletion();
  }, []);

  const fetchQuests = async () => {
    try {
      const response = await fetch('/api/quests/my', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setQuests(data);
      }
    } catch (error) {
      console.error('Ëé∑Âèñ‰ªªÂä°ÂàóË°®Â§±Ë¥•:', error);
    } finally {
      setLoading(false);
    }
  };

  const checkCompletion = async () => {
    try {
      const response = await fetch('/api/quests/check-completion', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setCompletionStatus(data);
      }
    } catch (error) {
      console.error('Ê£ÄÊü•ÂÆåÊàêÁä∂ÊÄÅÂ§±Ë¥•:', error);
    }
  };

  const generateQuests = async () => {
    try {
      const response = await fetch('/api/quests/generate', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setQuests(data.quests);
      } else {
        const error = await response.json();
        alert(error.msg);
      }
    } catch (error) {
      console.error('ÁîüÊàê‰ªªÂä°ÈìæÂ§±Ë¥•:', error);
    }
  };

  const startQuest = (quest) => {
    // ÂØºËà™Âà∞ÁªÉ‰π†È°µÈù¢ÔºåÂèØ‰ª•‰º†ÈÄíquest‰ø°ÊÅØ
    navigate('/practice', { 
      state: { 
        subject: quest.subject, 
        chapter: quest.chapter,
        questId: quest._id 
      } 
    });
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'completed':
        return <CheckCircle color="success" />;
      case 'available':
      case 'in_progress':
        return <PlayArrow color="primary" />;
      case 'locked':
      default:
        return <Lock color="disabled" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed':
        return 'success';
      case 'available':
        return 'primary';
      case 'in_progress':
        return 'warning';
      case 'locked':
      default:
        return 'default';
    }
  };

  if (loading) {
    return (
      <Container maxWidth="lg">
