import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  LinearProgress,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Chip,
  Button,
  CircularProgress,
  Alert,
  Tooltip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Slider,
  IconButton
} from '@mui/material';
import {
  Assessment,
  CheckCircle,
  Error,
  Timeline,
  TrendingUp,
  EmojiEvents,
  School,
  ArrowForward,
  Flag,
  Close,
  Lightbulb
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { Chart as ChartJS, ArcElement, Tooltip as ChartTooltip, Legend, CategoryScale, LinearScale, PointElement, LineElement, Title } from 'chart.js';
import { Doughnut, Line } from 'react-chartjs-2';

ChartJS.register(ArcElement, ChartTooltip, Legend, CategoryScale, LinearScale, PointElement, LineElement, Title);

function LearningProgress() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [progressData, setProgressData] = useState(null);
  const [conceptProgress, setConceptProgress] = useState([]);
  const [recentActivity, setRecentActivity] = useState([]);
  const [progressHistory, setProgressHistory] = useState([]);
  const [openGoalDialog, setOpenGoalDialog] = useState(false);
  const [learningGoal, setLearningGoal] = useState({
    targetMastery: 70,
    deadline: '',
    description: ''
  });
  
  const fetchProgressData = async () => {
    setLoading(true);
    setError('');
    
    try {
      const token = localStorage.getItem('token');
      const headers = {
        'Authorization': `Bearer ${token}`
      };

      const [progressResponse, conceptsResponse, activityResponse, historyResponse, goalResponse] = await Promise.all([
        fetch('/api/progress', { headers }),
        fetch('/api/progress/concepts', { headers }),
        fetch('/api/progress/activity', { headers }),
        fetch('/api/progress/history', { headers }),
        fetch('/api/progress/goals', { headers })
      ]);
      
      if (progressResponse.ok && conceptsResponse.ok && activityResponse.ok && historyResponse.ok && goalResponse.ok) {
        const [progressData, conceptsData, activityData, historyData, goalData] = await Promise.all([
          progressResponse.json(),
          conceptsResponse.json(),
          activityResponse.json(),
          historyResponse.json(),
          goalResponse.json()
        ]);
        
        setProgressData(progressData);
        setConceptProgress(conceptsData);
        setRecentActivity(activityData);
        setProgressHistory(historyData);
        
        if (goalData.goal) {
          setLearningGoal(goalData.goal);
        }
      } else {
        throw new Error('获取进度数据失败');
      }
    } catch (err) {
      console.error('获取进度数据失败:', err);
      setError('获取进度数据失败，请重试');
    } finally {
      setLoading(false);
    }
  };
  
  useEffect(() => {
    fetchProgressData();
  }, []);
  
  const handleGoalSubmit = async () => {
    try {
      const response = await fetch('/api/progress/goals', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(learningGoal)
      });

      if (response.ok) {
        setOpenGoalDialog(false);
        fetchProgressData();
      } else {
        throw new Error('设置学习目标失败');
      }
    } catch (err) {
      setError('设置学习目标失败，请重试');
    }
  };
  
  // 准备环形图数据
  const doughnutData = {
    labels: ['已掌握', '学习中', '未开始'],
    datasets: [{
      data: [
        progressData?.masteredPercentage || 0,
        progressData?.inProgressPercentage || 0,
        progressData?.notStartedPercentage || 0
      ],
      backgroundColor: [
        '#4caf50',
        '#2196f3',
        '#9e9e9e'
      ],
      borderWidth: 1
    }]
  };
  
  // 准备折线图数据
  const lineData = {
    labels: progressHistory.map(item => new Date(item.date).toLocaleDateString()),
    datasets: [{
      label: '学习掌握度',
      data: progressHistory.map(item => item.mastery),
      fill: false,
      borderColor: '#2196f3',
      tension: 0.1
    }]
  };
  
  const lineOptions = {
    responsive: true,
    plugins: {
      legend: { position: 'top' },
      title: {
        display: true,
        text: '学习掌握度趋势'
      }
    },
    scales: {
      y: {
        min: 0,
        max: 100,
        ticks: {
          callback: value => value + '%'
        }
      }
    }
  };
  
  if (loading) {
    return (
      <Container maxWidth="md" sx={{ mt: 4, textAlign: 'center' }}>
        <CircularProgress />
        <Typography variant="body1" sx={{ mt: 2 }}>
          正在加载学习进度数据...
        </Typography>
      </Container>
    );
  }
  
  if (error) {
    return (
      <Container maxWidth="md" sx={{ mt: 4 }}>
        <Alert severity="error">{error}</Alert>
      </Container>
    );
  }
  
  return (
    <Container maxWidth="lg">
      <Paper elevation={3} sx={{ p: 4, mt: 4 }}>
        {/* 页面标题和学习目标按钮 */}
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 3 }}>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Assessment color="primary" sx={{ fontSize: 40, mr: 2 }} />
            <Typography variant="h4">学习进度跟踪</Typography>
          </Box>
          <Button
            variant="outlined"
            startIcon={<Flag />}
            onClick={() => setOpenGoalDialog(true)}
          >
            设置学习目标
          </Button>
        </Box>

        {/* 当前学习目标展示 */}
        {learningGoal.deadline && (
          <Alert severity="info" sx={{ mb: 3 }}>
            <Typography variant="subtitle1" gutterBottom>
              当前学习目标：{learningGoal.description}
            </Typography>
            <Typography variant="body2">
              目标掌握度：{learningGoal.targetMastery}% | 完成期限：{new Date(learningGoal.deadline).toLocaleDateString()}
            </Typography>
          </Alert>
        )}

        {/* 总体进度概览 */}
        <Grid container spacing={4}>
          <Grid item xs={12} md={8}>
            <Card variant="outlined" sx={{ height: '100%' }}>
              <CardContent>
                <Typography variant="h6" gutterBottom>总体学习进度</Typography>
                
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <Box sx={{ width: '100%', mr: 1 }}>
                    <LinearProgress 
                      variant="determinate" 
                      value={progressData?.overallMastery || 0} 
                      sx={{ height: 10, borderRadius: 5 }}
                    />
                  </Box>
                  <Box sx={{ minWidth: 35 }}>
                    <Typography variant="body2" color="text.secondary">
                      {`${Math.round(progressData?.overallMastery || 0)}%`}
                    </Typography>
                  </Box>
                </Box>
                
                <Typography variant="body2" color="text.secondary" paragraph>
                  {progressData?.overallMastery >= learningGoal.targetMastery 
                    ? '恭喜！您已达到学习目标。' 
                    : `继续努力！还需提高${(learningGoal.targetMastery - (progressData?.overallMastery || 0)).toFixed(1)}%的掌握度以达到目标。`}
                </Typography>
                
                <Grid container spacing={2} sx={{ mt: 2 }}>
                  <Grid item xs={4}>
                    <Box sx={{ textAlign: 'center' }}>
                      <Typography variant="h4" color="primary">
                        {progressData?.completedTopics || 0}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        已完成知识点
                      </Typography>
                    </Box>
                  </Grid>
                  <Grid item xs={4}>
                    <Box sx={{ textAlign: 'center' }}>
                      <Typography variant="h4" color="secondary">
                        {progressData?.solvedProblems || 0}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        已解决题目
                      </Typography>
                    </Box>
                  </Grid>
                  <Grid item xs={4}>
                    <Box sx={{ textAlign: 'center' }}>
                      <Typography variant="h4" color="success.main">
                        {progressData?.studyDays || 0}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        学习天数
                      </Typography>
                    </Box>
                  </Grid>
                </Grid>
              </CardContent>
            </Card>
          </Grid>
          
          <Grid item xs={12} md={4}>
            <Card variant="outlined" sx={{ height: '100%' }}>
              <CardContent>
                <Typography variant="h6" gutterBottom>知识点掌握情况</Typography>
                
                <Box sx={{ display: 'flex', justifyContent: 'center', p: 2 }}>
                  <Box sx={{ height: 200, width: 200 }}>
                    <Doughnut data={doughnutData} />
                  </Box>
                </Box>
                
                <Box sx={{ display: 'flex', justifyContent: 'space-around', mt: 2 }}>
                  <Chip 
                    label={`已掌握: ${progressData?.masteredPercentage || 0}%`} 
                    color="success" 
                    size="small" 
                  />
                  <Chip 
                    label={`学习中: ${progressData?.inProgressPercentage || 0}%`} 
                    color="primary" 
                    size="small" 
                  />
                  <Chip 
                    label={`未开始: ${progressData?.notStartedPercentage || 0}%`} 
                    color="default" 
                    size="small" 
                  />
                </Box>
              </CardContent>
            </Card>
          </Grid>
        </Grid>

        {/* 学习建议模块 */}
        <Card variant="outlined" sx={{ mt: 4 }}>
          <CardContent>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
              <Lightbulb color="primary" sx={{ mr: 1 }} />
              <Typography variant="h6">学习建议</Typography>
            </Box>
            
            <Grid container spacing={2}>
              {conceptProgress
                .filter(concept => concept.mastery < learningGoal.targetMastery)
                .slice(0, 3)
                .map((concept, index) => (
                  <Grid item xs={12} md={4} key={index}>
                    <Card variant="outlined">
                      <CardContent>
                        <Typography variant="subtitle1" gutterBottom>
                          {concept.name}
                        </Typography>
                        <Typography variant="body2" color="text.secondary" paragraph>
                          当前掌握度：{concept.mastery}%
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          建议：根据您的学习情况，建议您多练习{concept.name}相关的题目，特别是{concept.weakPoints?.join('、')}等知识点。
                        </Typography>
                        <Button
                          size="small"
                          endIcon={<ArrowForward />}
                          onClick={() => navigate(`/practice/${concept.id}`)}
                          sx={{ mt: 2 }}
                        >
                          开始练习
                        </Button>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
            </Grid>
          </CardContent>
        </Card>

        {/* 学习趋势图 */}
        <Card variant="outlined" sx={{ mt: 4 }}>
          <CardContent>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
              <Timeline color="primary" sx={{ mr: 1 }} />
              <Typography variant="h6">学习掌握度趋势</Typography>
            </Box>
            
            <Box sx={{ height: 300 }}>
              <Line data={lineData} options={lineOptions} />
            </Box>
          </CardContent>
        </Card>

        {/* 知识点详情 */}
        <Card variant="outlined" sx={{ mt: 4 }}>
          <CardContent>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
              <TrendingUp color="primary" sx={{ mr: 1 }} />
              <Typography variant="h6">知识点掌握详情</Typography>
            </Box>
            
            <Grid container spacing={2}>
              {conceptProgress.map((concept) => (
                <Grid item xs={12} sm={6} md={4} key={concept.id}>
                  <Card variant="outlined">
                    <CardContent>
                      <Typography variant="subtitle1" gutterBottom>
                        {concept.name}
                      </Typography>
                      
                      <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                        <Box sx={{ width: '100%', mr: 1 }}>
                          <LinearProgress 
                            variant="determinate" 
                            value={concept.mastery} 
                            sx={{
                              height: 8,
                              borderRadius: 5,
                              bgcolor: '#e0e0e0',
                              '& .MuiLinearProgress-bar': {
                                bgcolor: concept.mastery >= learningGoal.targetMastery ? '#4caf50' : '#2196f3'
                              }
                            }}
                          />
                        </Box>
                        <Box sx={{ minWidth: 35 }}>
                          <Typography variant="body2" color="text.secondary">
                            {`${Math.round(concept.mastery)}%`}
                          </Typography>
                        </Box>
                      </Box>
                      
                      <Box sx={{ display: 'flex', alignItems: 'center' }}>
                        {concept.mastery >= learningGoal.targetMastery ? (
                          <Tooltip title="已达到目标">
                            <CheckCircle fontSize="small" color="success" sx={{ mr: 1 }} />
                          </Tooltip>
                        ) : (
                          <Tooltip title="继续学习">
                            <School fontSize="small" color="primary" sx={{ mr: 1 }} />
                          </Tooltip>
                        )}
                        <Typography variant="body2" color="text.secondary">
                          {concept.solvedProblems} 题已解决
                        </Typography>
                      </Box>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </CardContent>
        </Card>

        {/* 最近活动 */}
        <Card variant="outlined" sx={{ mt: 4 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>最近活动</Typography>
            
            <List>
              {recentActivity.length > 0 ? (
                recentActivity.map((activity) => (
                  <ListItem key={activity.id} divider>
                    <ListItemIcon>
                      {activity.type === 'problem_solved' && <CheckCircle color="success" />}
                      {activity.type === 'concept_mastered' && <School color="primary" />}
                      {activity.type === 'goal_achieved' && <EmojiEvents color="secondary" />}
                    </ListItemIcon>
                    <ListItemText
                      primary={activity.description}
                      secondary={new Date(activity.timestamp).toLocaleString()}
                    />
                  </ListItem>
                ))
              ) : (
                <ListItem>
                  <ListItemText primary="暂无活动记录" />
                </ListItem>
              )}
            </List>
          </CardContent>
        </Card>

        {/* 学习目标设置对话框 */}
        <Dialog open={openGoalDialog} onClose={() => setOpenGoalDialog(false)} maxWidth="sm" fullWidth>
          <DialogTitle>
            设置学习目标
            <IconButton
              aria-label="close"
              onClick={() => setOpenGoalDialog(false)}
              sx={{ position: 'absolute', right: 8, top: 8 }}
            >
              <Close />
            </IconButton>
          </DialogTitle>
          <DialogContent>
            <Box sx={{ mt: 2 }}>
              <Typography gutterBottom>目标掌握度 (%)</Typography>
              <Slider
                value={learningGoal.targetMastery}
                onChange={(e, newValue) => setLearningGoal({ ...learningGoal, targetMastery: newValue })}
                valueLabelDisplay="auto"
                step={5}
                marks
                min={0}
                max={100}
              />
              <TextField
                fullWidth
                label="目标完成日期"
                type="date"
                value={learningGoal.deadline}
                onChange={(e) => setLearningGoal({ ...learningGoal, deadline: e.target.value })}
                sx={{ mt: 2 }}
                InputLabelProps={{ shrink: true }}
              />
              <TextField
                fullWidth
                label="目标描述"
                multiline
                rows={3}
                value={learningGoal.description}
                onChange={(e) => setLearningGoal({ ...learningGoal, description: e.target.value })}
                sx={{ mt: 2 }}
              />
            </Box>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenGoalDialog(false)}>取消</Button>
            <Button onClick={handleGoalSubmit} variant="contained">保存</Button>
          </DialogActions>
        </Dialog>
      </Paper>
    </Container>
  );
}

export default LearningProgress;
