import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';

// 组件导入
import Header from './components/Layout/Header';
import Footer from './components/Layout/Footer';
import Dashboard from './components/Dashboard/Dashboard';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import QuestionUpload from './components/Questions/QuestionUpload';
import QuestionSolver from './components/Questions/QuestionSolver';
import SurveyForm from './components/Survey/SurveyForm';
import LearningProgress from './components/Progress/LearningProgress';
import RewardCenter from './components/Rewards/RewardCenter';
import TestGenerator from './components/Tests/TestGenerator';

// 创建主题
const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
    background: {
      default: '#f5f5f5',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 600,
    },
  },
});

function App() {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Header />
      <main style={{ minHeight: 'calc(100vh - 130px)', paddingTop: '20px', paddingBottom: '40px' }}>
        <Routes>
          <Route path="/" element={<Dashboard />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/survey" element={<SurveyForm />} />
          <Route path="/upload-question" element={<QuestionUpload />} />
          <Route path="/solve-question/:id" element={<QuestionSolver />} />
          <Route path="/progress" element={<LearningProgress />} />
          <Route path="/rewards" element={<RewardCenter />} />
          <Route path="/generate-test" element={<TestGenerator />} />
        </Routes>
      </main>
      <Footer />
    </ThemeProvider>
  );
}

export default App;
