import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { Box } from '@mui/material';
import CssBaseline from '@mui/material/CssBaseline';

// 组件导入
import Header from './components/Layout/Header';
import Footer from './components/Layout/Footer';
import Dashboard from './components/Dashboard/Dashboard';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import QuestionUpload from './components/Questions/QuestionUpload';
import QuestionSolver from './components/Questions/QuestionSolver';
import SurveyForm from './components/Survey/SurveyForm';
import LearningProgress from './components/Progress/LearningProgress';
import RewardCenter from './components/Rewards/RewardCenter';
import TestGenerator from './components/Tests/TestGenerator';
import QuestChain from './components/Quests/QuestChain';

// 创建主题
const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
    background: {
      default: '#f5f5f5',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 600,
    },
  },
});

function App() {
  const [isAuthenticated, setIsAuthenticated] = React.useState(false);
  const [hasCompletedSurvey, setHasCompletedSurvey] = React.useState(false);

  const updateAuthStatus = (status) => {
    setIsAuthenticated(status);
  };

  React.useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
      // 强制所有用户首次登录时填写问卷
      setHasCompletedSurvey(false);
      fetch('/api/survey/status', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      })
        .then(res => res.json())
        .then(data => {
          // 仅当后端明确返回已完成时才设置为true
          if (data.completed === true) {
            setHasCompletedSurvey(true);
          }
        })
        .catch(err => console.error('获取问卷状态失败:', err));
    }

    // 添加认证状态变化监听器
    const handleAuthStatusChange = () => {
      const token = localStorage.getItem('token');
      if (token) {
        setIsAuthenticated(true);
        setHasCompletedSurvey(false);
        // 重新获取问卷状态
        fetch('/api/survey/status', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        })
          .then(res => res.json())
          .then(data => {
            if (data.completed === true) {
              setHasCompletedSurvey(true);
            }
          })
          .catch(err => console.error('获取问卷状态失败:', err));
      } else {
        setIsAuthenticated(false);
        setHasCompletedSurvey(false);
      }
    };

    window.addEventListener('auth-status-changed', handleAuthStatusChange);

    // 清理函数
    return () => {
      window.removeEventListener('auth-status-changed', handleAuthStatusChange);
    };
  }, []);

  // 在return语句中移除Router包装
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
        <Header />
        <Box component="main" sx={{ flexGrow: 1 }}>
          <Routes>
            <Route path="/" element={isAuthenticated ? (hasCompletedSurvey ? <Dashboard /> : <SurveyForm />) : <Login />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/survey" element={isAuthenticated ? <SurveyForm /> : <Login />} />
            <Route path="/upload-question" element={isAuthenticated ? <QuestionUpload /> : <Login />} />
            <Route path="/solve-question/:id" element={isAuthenticated ? <QuestionSolver /> : <Login />} />
            <Route path="/progress" element={isAuthenticated ? <LearningProgress /> : <Login />} />
            <Route path="/rewards" element={isAuthenticated ? <RewardCenter /> : <Login />} />
            <Route path="/generate-test" element={isAuthenticated ? <TestGenerator /> : <Login />} />
            <Route path="/quests" element={isAuthenticated ? <QuestChain /> : <Login />} />
          </Routes>
        </Box>
        <Footer />
      </Box>
    </ThemeProvider>
  );
}

export default App;
