import React, { useState, useEffect } from 'react';
import { 
  Container, Paper, Typography, Box, Grid, Card, 
  CardContent, CardMedia, Button, Dialog, 
  DialogTitle, DialogContent, DialogActions
} from '@mui/material';
import { EmojiEvents, Lock } from '@mui/icons-material';

function RewardCenter() {
  const [rewards, setRewards] = useState([]);
  const [userProgress, setUserProgress] = useState(0);
  const [selectedReward, setSelectedReward] = useState(null);
  const [dialogOpen, setDialogOpen] = useState(false);
  
  useEffect(() => {
    const fetchRewardsAndProgress = async () => {
      try {
        // 获取奖励列表
        const rewardsResponse = await fetch('/api/rewards', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        
        // 获取用户进度
        const progressResponse = await fetch('/api/progress', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        
        if (rewardsResponse.ok && progressResponse.ok) {
          const rewardsData = await rewardsResponse.json();
          const progressData = await progressResponse.json();
          
          setRewards(rewardsData);
          setUserProgress(progressData.overallMastery);
        }
      } catch (error) {
        console.error('获取奖励数据失败:', error);
      }
    };
    
    fetchRewardsAndProgress();
  }, []);
  
  const handleRewardClick = (reward) => {
    setSelectedReward(reward);
    setDialogOpen(true);
  };
  
  const handleClaimReward = async () => {
    try {
      const response = await fetch(`/api/rewards/claim/${selectedReward.id}`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      
      if (response.ok) {
        // 更新奖励状态
        setRewards(rewards.map(reward => 
          reward.id === selectedReward.id 
            ? { ...reward, claimed: true } 
            : reward
        ));
        setDialogOpen(false);
      }
    } catch (error) {
      console.error('领取奖励失败:', error);
    }
  };
  
  return (
    <Container maxWidth="lg">
      <Paper elevation={3} style={{ padding: '2rem', marginTop: '2rem' }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
          <EmojiEvents color="primary" sx={{ fontSize: 40, mr: 2 }} />
          <Typography variant="h4">
            奖励中心
          </Typography>
        </Box>
        
        <Box sx={{ mb: 4, p: 2, bgcolor: '#f5f5f5', borderRadius: 2 }}>
          <Typography variant="h6" gutterBottom>
            您当前的学习掌握度: {userProgress}%
          </Typography>
          <Typography variant="body1">
            {userProgress >= 70 
              ? '恭喜！您已达到领取奖励的标准，可以选择下方的奖励进行领取。' 
              : `继续努力！再提高${(70 - userProgress).toFixed(1)}%的掌握度即可获得奖励。`}
          </Typography>
        </Box>
        
        <Typography variant="h5" gutterBottom>
          可获得的奖励
        </Typography>
        
        <Grid container spacing={3}>
          {rewards.map((reward) => (
            <Grid item xs={12} sm={6} md={4} key={reward.id}>
              <Card 
                variant="outlined" 
                sx={{ 
                  height: '100%', 
                  display: 'flex', 
                  flexDirection: 'column',
                  opacity: userProgress >= 70 ? 1 : 0.7
                }}
              >
                <CardMedia
                  component="img"
                  height="140"
                  image={reward.imageUrl}
                  alt={reward.name}
                />
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography variant="h6" gutterBottom>
                    {reward.name}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" paragraph>
                    {reward.description}
                  </Typography>
                  
                  {userProgress >= 70 ? (
                    <Button 
                      variant="contained" 
                      color="primary" 
                      fullWidth
                      disabled={reward.claimed}
                      onClick={() => handleRewardClick(reward)}
                    >
                      {reward.claimed ? '已领取' : '领取奖励'}
                    </Button>
                  ) : (
                    <Button 
                      variant="outlined" 
                      startIcon={<Lock />} 
                      disabled 
                      fullWidth
                    >
                      需要70%掌握度
                    </Button>
                  )}
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
        
        <Dialog open={dialogOpen} onClose={() => setDialogOpen(false)}>
          <DialogTitle>确认领取奖励</DialogTitle>
          <DialogContent>
            {selectedReward && (
              <>
                <Typography variant="h6" gutterBottom>
                  {selectedReward.name}
                </Typography>
                <Typography variant="body1" paragraph>
                  您确定要领取"{selectedReward.name}"奖励吗？
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  领取后，该奖励将发送到您的账户中，请注意查收。
                </Typography>
              </>
            )}
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setDialogOpen(false)}>取消</Button>
            <Button 
              onClick={handleClaimReward} 
              variant="contained" 
              color="primary"
            >
              确认领取
            </Button>
          </DialogActions>
        </Dialog>
      </Paper>
    </Container>
  );
}

export default RewardCenter;
