import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  Box,
  Stepper,
  Step,
  StepLabel,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  FormLabel,
  Checkbox,
  FormGroup,
  TextField,
  Button,
  Divider,
  Alert,
  CircularProgress,
} from '@mui/material';
import { AssignmentTurnedIn, School, EmojiEvents, Person } from '@mui/icons-material';

function SurveyForm() {
  const navigate = useNavigate();
  const [activeStep, setActiveStep] = useState(0);
  const [completed, setCompleted] = useState(false);
  const [formData, setFormData] = useState({
    // 基本信息
    grade: '',
    studyReason: '',
    preferredGuide: '',
    otherGuide: '',
    
    // 学习状况
    limitUnderstanding: '',
    derivativeDifficulties: [],
    otherDifficulty: '',
    derivativeRules: false,
    higherOrderDerivatives: '',
    implicitDerivativeChallenge: '',
    otherChallenge: '',
    
    // 学习偏好
    learningMethods: [],
    otherMethod: '',
    tutorServices: [],
    otherService: '',
    
    // 兴趣爱好
    hobbies: [],
    otherHobby: ''
  });

  useEffect(() => {
    // 检查用户是否已登录
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login', { state: { from: '/survey' } });
    } else {
      // 检查用户是否已完成问卷
      fetch('/api/survey/status', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      })
        .then(res => res.json())
        .then(data => {
          if (data.completed) {
            setCompleted(true);
            // 获取已保存的问卷数据
            return fetch('/api/survey/data', {
              headers: {
                'Authorization': `Bearer ${token}`
              }
            });
          }
        })
        .then(res => res?.json())
        .then(data => {
          if (data) {
            setFormData(data);
          }
        })
        .catch(err => console.error('获取问卷状态失败:', err));
    }
  }, [navigate]);

  const steps = [
    '基本信息',
    '学习状况',
    '学习偏好',
    '兴趣爱好'
  ];

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleCheckboxChange = (event) => {
    const { name, value, checked } = event.target;
    
    if (checked) {
      setFormData({
        ...formData,
        [name]: [...(formData[name] || []), value]
      });
    } else {
      setFormData({
        ...formData,
        [name]: (formData[name] || []).filter(item => item !== value)
      });
    }
  };

  const handleRadioChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const [submitting, setSubmitting] = useState(false);
  const [submitError, setSubmitError] = useState('');
  const handleSubmit = async () => {
    if (submitting) return;
    setSubmitting(true);
    setSubmitError('');
    
    try {
      const response = await fetch('/api/survey/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(formData)
      });
      
      // 检查响应状态
      if (!response.ok) {
        // 检查响应内容类型
        const contentType = response.headers.get('content-type');
        if (contentType && contentType.includes('application/json')) {
          const errorData = await response.json();
          throw new Error(errorData.message || '提交失败');
        } else {
          // 如果不是JSON格式，直接获取文本内容作为错误信息
          const errorText = await response.text();
          throw new Error('服务器返回非JSON格式响应: ' + (errorText.substring(0, 100) || '提交失败'));
        }
      }
      
      // 检查响应内容类型
      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        throw new Error('服务器返回了非JSON格式的响应');
      }
      
      // 获取响应数据
      let responseData;
      try {
        responseData = await response.json();
        console.log('问卷提交成功:', responseData);
      } catch (jsonError) {
        console.error('解析响应JSON失败:', jsonError);
        throw new Error('服务器返回的数据格式有误，请联系管理员');
      }
  
      // 更新用户状态
      setCompleted(true);
      
      // 更新本地存储，标记问卷已完成
      localStorage.setItem('surveyCompleted', 'true');
      
      // 清除问卷数据缓存
      localStorage.removeItem('surveyData');
      
      // 使用setTimeout确保状态更新后再跳转
      setTimeout(() => {
        // 使用window.location.href进行强制跳转，确保页面完全刷新
        window.location.href = '/';
      }, 100);
    } catch (error) {
      console.error('提交问卷失败:', error);
      setSubmitError(error.message || '提交失败，请稍后重试');
    } finally {
      setSubmitting(false);
    }
  };

  const renderStepContent = (step) => {
    switch (step) {
      case 0:
        return (
          <Box sx={{ mt: 4 }}>
            <Typography variant="h6" gutterBottom>
              一、基本信息
            </Typography>
            
            <FormControl component="fieldset" sx={{ mb: 3, width: '100%' }}>
              <FormLabel component="legend">1. 您目前所在的年级是？</FormLabel>
              <RadioGroup
                name="grade"
                value={formData.grade}
                onChange={handleRadioChange}
              >
                <FormControlLabel value="大一" control={<Radio />} label="大一" />
                <FormControlLabel value="大二" control={<Radio />} label="大二" />
                <FormControlLabel value="大三" control={<Radio />} label="大三" />
                <FormControlLabel value="其他" control={<Radio />} label="其他" />
              </RadioGroup>
            </FormControl>
            
            <FormControl component="fieldset" sx={{ mb: 3, width: '100%' }}>
              <FormLabel component="legend">2. 您学习微积分的原因是什么？</FormLabel>
              <RadioGroup
                name="studyReason"
                value={formData.studyReason}
                onChange={handleRadioChange}
              >
                <FormControlLabel value="对微积分有强烈的兴趣" control={<Radio />} label="对微积分有强烈的兴趣" />
                <FormControlLabel value="认为微积分对未来学习有帮助" control={<Radio />} label="认为微积分对未来学习有帮助" />
                <FormControlLabel value="培养方案中的必修课程" control={<Radio />} label="培养方案中的必修课程" />
                <FormControlLabel value="其他" control={<Radio />} label="其他" />
              </RadioGroup>
            </FormControl>
            
            <FormControl component="fieldset" sx={{ mb: 3, width: '100%' }}>
              <FormLabel component="legend">3. 您更希望以什么样的身份来引导你学习？</FormLabel>
              <RadioGroup
                name="preferredGuide"
                value={formData.preferredGuide}
                onChange={handleRadioChange}
              >
                <FormControlLabel value="老师" control={<Radio />} label="老师" />
                <FormControlLabel value="朋友" control={<Radio />} label="朋友" />
                <FormControlLabel value="同学" control={<Radio />} label="同学" />
                <FormControlLabel value="其他" control={<Radio />} label="其他" />
              </RadioGroup>
              {formData.preferredGuide === '其他' && (
                <TextField
                  name="otherGuide"
                  label="请说明"
                  value={formData.otherGuide}
                  onChange={handleInputChange}
                  fullWidth
                  margin="normal"
                />
              )}
            </FormControl>
          </Box>
        );
      case 1:
        return (
          <Box sx={{ mt: 4 }}>
            <Typography variant="h6" gutterBottom>
              二、关于微分与导数的学习状况
            </Typography>
            
            <FormControl component="fieldset" sx={{ mb: 3, width: '100%' }}>
              <FormLabel component="legend">4. 在学习微分与导数之前，您对极限概念的理解程度如何？</FormLabel>
              <RadioGroup
                name="limitUnderstanding"
                value={formData.limitUnderstanding}
                onChange={handleRadioChange}
              >
                <FormControlLabel value="非常熟悉" control={<Radio />} label="非常熟悉" />
                <FormControlLabel value="基本理解" control={<Radio />} label="基本理解" />
                <FormControlLabel value="不太清楚" control={<Radio />} label="不太清楚" />
                <FormControlLabel value="完全不了解" control={<Radio />} label="完全不了解" />
              </RadioGroup>
            </FormControl>
            
            <FormControl component="fieldset" sx={{ mb: 3, width: '100%' }}>
              <FormLabel component="legend">5. 您认为自己在理解和应用导数定义方面存在哪些困难？（可多选）</FormLabel>
              <FormGroup>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.derivativeDifficulties?.includes('理解导数的几何意义')}
                      onChange={handleCheckboxChange}
                      name="derivativeDifficulties"
                      value="理解导数的几何意义"
                    />
                  }
                  label="理解导数的几何意义"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.derivativeDifficulties?.includes('掌握导数的基本计算方法')}
                      onChange={handleCheckboxChange}
                      name="derivativeDifficulties"
                      value="掌握导数的基本计算方法"
                    />
                  }
                  label="掌握导数的基本计算方法"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.derivativeDifficulties?.includes('应用导数解决实际问题')}
                      onChange={handleCheckboxChange}
                      name="derivativeDifficulties"
                      value="应用导数解决实际问题"
                    />
                  }
                  label="应用导数解决实际问题"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.derivativeDifficulties?.includes('其他')}
                      onChange={handleCheckboxChange}
                      name="derivativeDifficulties"
                      value="其他"
                    />
                  }
                  label="其他"
                />
              </FormGroup>
              {formData.derivativeDifficulties?.includes('其他') && (
                <TextField
                  name="otherDifficulty"
                  label="请说明"
                  value={formData.otherDifficulty}
                  onChange={handleInputChange}
                  fullWidth
                  margin="normal"
                />
              )}
            </FormControl>
            
            <FormControl component="fieldset" sx={{ mb: 3, width: '100%' }}>
              <FormLabel component="legend">6. 您是否能够熟练运用求导法则（如链锁规则、四则运算法则等）进行复杂函数的求导？</FormLabel>
              <RadioGroup
                name="derivativeRules"
                value={formData.derivativeRules ? 'true' : 'false'}
                onChange={(e) => setFormData({
                  ...formData,
                  derivativeRules: e.target.value === 'true'
                })}
              >
                <FormControlLabel value="true" control={<Radio />} label="是" />
                <FormControlLabel value="false" control={<Radio />} label="否" />
              </RadioGroup>
            </FormControl>
            
            <FormControl component="fieldset" sx={{ mb: 3, width: '100%' }}>
              <FormLabel component="legend">7. 对于高阶导数的概念及其计算，您的掌握程度如何？</FormLabel>
              <RadioGroup
                name="higherOrderDerivatives"
                value={formData.higherOrderDerivatives}
                onChange={handleRadioChange}
              >
                <FormControlLabel value="非常熟悉" control={<Radio />} label="非常熟悉" />
                <FormControlLabel value="基本掌握" control={<Radio />} label="基本掌握" />
                <FormControlLabel value="有些困惑" control={<Radio />} label="有些困惑" />
                <FormControlLabel value="几乎不懂" control={<Radio />} label="几乎不懂" />
              </RadioGroup>
            </FormControl>
            
            <FormControl component="fieldset" sx={{ mb: 3, width: '100%' }}>
              <FormLabel component="legend">8. 在学习隐函数求导时，您遇到的最大挑战是什么？</FormLabel>
              <RadioGroup
                name="implicitDerivativeChallenge"
                value={formData.implicitDerivativeChallenge}
                onChange={handleRadioChange}
              >
                <FormControlLabel value="理解隐函数的概念" control={<Radio />} label="理解隐函数的概念" />
                <FormControlLabel value="实际操作中的细节处理" control={<Radio />} label="实际操作中的细节处理" />
                <FormControlLabel value="与其他知识点的结合应用" control={<Radio />} label="与其他知识点的结合应用" />
                <FormControlLabel value="其他" control={<Radio />} label="其他" />
              </RadioGroup>
              {formData.implicitDerivativeChallenge === '其他' && (
                <TextField
                  name="otherChallenge"
                  label="请说明"
                  value={formData.otherChallenge}
                  onChange={handleInputChange}
                  fullWidth
                  margin="normal"
                />
              )}
            </FormControl>
          </Box>
        );
      case 2:
        return (
          <Box sx={{ mt: 4 }}>
            <Typography variant="h6" gutterBottom>
              三、学习偏好
            </Typography>
            
            <FormControl component="fieldset" sx={{ mb: 3, width: '100%' }}>
              <FormLabel component="legend">9. 您更喜欢哪种学习方式？（可多选）</FormLabel>
              <FormGroup>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.learningMethods?.includes('听课')}
                      onChange={handleCheckboxChange}
                      name="learningMethods"
                      value="听课"
                    />
                  }
                  label="听课"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.learningMethods?.includes('阅读教材')}
                      onChange={handleCheckboxChange}
                      name="learningMethods"
                      value="阅读教材"
                    />
                  }
                  label="阅读教材"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.learningMethods?.includes('参加讨论班')}
                      onChange={handleCheckboxChange}
                      name="learningMethods"
                      value="参加讨论班"
                    />
                  }
                  label="参加讨论班"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.learningMethods?.includes('自己做课后题')}
                      onChange={handleCheckboxChange}
                      name="learningMethods"
                      value="自己做课后题"
                    />
                  }
                  label="自己做课后题"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.learningMethods?.includes('其他')}
                      onChange={handleCheckboxChange}
                      name="learningMethods"
                      value="其他"
                    />
                  }
                  label="其他"
                />
              </FormGroup>
              {formData.learningMethods?.includes('其他') && (
                <TextField
                  name="otherMethod"
                  label="请说明"
                  value={formData.otherMethod}
                  onChange={handleInputChange}
                  fullWidth
                  margin="normal"
                />
              )}
            </FormControl>
            
            <FormControl component="fieldset" sx={{ mb: 3, width: '100%' }}>
              <FormLabel component="legend">10. 您更偏向哪一种个性化的辅导服务？（可多选）</FormLabel>
              <FormGroup>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.tutorServices?.includes('提供详细的题目讲解')}
                      onChange={handleCheckboxChange}
                      name="tutorServices"
                      value="提供详细的题目讲解"
                    />
                  }
                  label="提供详细的题目讲解"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.tutorServices?.includes('实时解答疑问')}
                      onChange={handleCheckboxChange}
                      name="tutorServices"
                      value="实时解答疑问"
                    />
                  }
                  label="实时解答疑问"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.tutorServices?.includes('个性化学习建议')}
                      onChange={handleCheckboxChange}
                      name="tutorServices"
                      value="个性化学习建议"
                    />
                  }
                  label="个性化学习建议"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.tutorServices?.includes('学习进度跟踪')}
                      onChange={handleCheckboxChange}
                      name="tutorServices"
                      value="学习进度跟踪"
                    />
                  }
                  label="学习进度跟踪"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.tutorServices?.includes('其他')}
                      onChange={handleCheckboxChange}
                      name="tutorServices"
                      value="其他"
                    />
                  }
                  label="其他"
                />
              </FormGroup>
              {formData.tutorServices?.includes('其他') && (
                <TextField
                  name="otherService"
                  label="请说明"
                  value={formData.otherService}
                  onChange={handleInputChange}
                  fullWidth
                  margin="normal"
                />
              )}
            </FormControl>
          </Box>
        );
      case 3:
        return (
          <Box sx={{ mt: 4 }}>
            <Typography variant="h6" gutterBottom>
              四、兴趣爱好
            </Typography>
            
            <FormControl component="fieldset" sx={{ mb: 3, width: '100%' }}>
              <FormLabel component="legend">11. 您有哪些兴趣爱好？（可多选）</FormLabel>
              <FormGroup>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.hobbies?.includes('摄影')}
                      onChange={handleCheckboxChange}
                      name="hobbies"
                      value="摄影"
                    />
                  }
                  label="摄影"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.hobbies?.includes('旅行')}
                      onChange={handleCheckboxChange}
                      name="hobbies"
                      value="旅行"
                    />
                  }
                  label="旅行"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.hobbies?.includes('美食')}
                      onChange={handleCheckboxChange}
                      name="hobbies"
                      value="美食"
                    />
                  }
                  label="美食"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.hobbies?.includes('阅读')}
                      onChange={handleCheckboxChange}
                      name="hobbies"
                      value="阅读"
                    />
                  }
                  label="阅读"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={formData.hobbies?.includes('其他')}
                      onChange={handleCheckboxChange}
                      name="hobbies"
                      value="其他"
                    />
                  }
                  label="其他"
                />
              </FormGroup>
              {formData.hobbies?.includes('其他') && (
                <TextField
                  name="otherHobby"
                  label="请说明"
                  value={formData.otherHobby}
                  onChange={handleInputChange}
                  fullWidth
                  margin="normal"
                />
              )}
            </FormControl>
          </Box>
        );
      default:
        return null;
    }
  };

  if (completed) {
    return (
      <Container maxWidth="md">
        <Paper elevation={3} sx={{ p: 4, mt: 4 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
            <AssignmentTurnedIn color="success" sx={{ fontSize: 40, mr: 2 }} />
            <Typography variant="h4">
              问卷已完成
            </Typography>
          </Box>
          <Alert severity="success" sx={{ mb: 3 }}>
            感谢您完成问卷！我们将根据您的回答为您提供个性化的学习体验。
          </Alert>
          <Button
            variant="contained"
            color="primary"
            onClick={() => navigate('/')}
          >
            返回首页
          </Button>
        </Paper>
      </Container>
    );
  }

  return (
    <Container maxWidth="md">
      <Paper elevation={3} sx={{ p: 4, mt: 4 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
          <School color="primary" sx={{ fontSize: 40, mr: 2 }} />
          <Typography variant="h4">
            微分与导数章节调查问卷
          </Typography>
        </Box>
        
        <Typography variant="body1" paragraph>
          为了更好地了解您的学习情况和需求，以便提供更加个性化的辅导服务，请您花几分钟时间完成以下问卷。感谢您的支持与配合！
        </Typography>
        
        <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
          {steps.map((label) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>
        
        <Divider sx={{ mb: 3 }} />
        
        {renderStepContent(activeStep)}
        
        {submitError && (
          <Alert severity="error" sx={{ mt: 2, mb: 2 }}>
            {submitError}
          </Alert>
        )}
        
        <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 4 }}>
          <Button
            variant="outlined"
            disabled={activeStep === 0}
            onClick={handleBack}
          >
            上一步
          </Button>
          
          {activeStep === steps.length - 1 ? (
            <Button
              variant="contained"
              color="primary"
              onClick={handleSubmit}
              disabled={submitting}
            >
              {submitting ? (
                <CircularProgress size={24} />
              ) : (
                '提交问卷'
              )}
            </Button>
          ) : (
            <Button
              variant="contained"
              color="primary"
              onClick={handleNext}
            >
              下一步
            </Button>
          )}
        </Box>
      </Paper>
    </Container>
  );
}

export default SurveyForm;
