import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Button,
  Grid,
  CircularProgress,
  Alert,
  Card,
  CardContent,
  Divider,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
} from '@mui/material';
import { Print, Save, Close, Refresh } from '@mui/icons-material';
import axios from 'axios';
import { testsAPI } from '../../api/api';
import 'katex/dist/katex.min.css';
import { InlineMath, BlockMath } from 'react-katex';
import { styled } from '@mui/material/styles';

// Move formatMathExpression function outside the component
const formatMathExpression = (expression) => {
  // 处理空值或undefined
  if (expression === null || expression === undefined) return '';
  
  // 处理非字符串类型（如对象）
  if (typeof expression !== 'string') {
    try {
      if (typeof expression === 'number') {
        return expression.toString();
      } else if (expression.toString() === '[object Object]') {
        try {
          return JSON.stringify(expression);
        } catch (jsonError) {
          return '表达式';
        }
      }
      return expression.toString();
    } catch (e) {
      console.error('格式化表达式错误:', e);
      return ''; 
    }
  }
  
  // 确保expression是字符串类型
  let formatted = expression.trim();
  
  // 基本清理
  formatted = formatted.replace(/\n/g, ' ');
  formatted = formatted.replace(/\t/g, ' ');
  formatted = formatted.replace(/\r/g, ' ');
  formatted = formatted.replace(/\s{2,}/g, ' ');
  
  // 修复反斜杠问题 - 确保LaTeX命令有正确的反斜杠数量
  formatted = formatted.replace(/\\\\([a-zA-Z])/g, "\\$1"); // 四个反斜杠变一个
  formatted = formatted.replace(/\\([a-zA-Z])/g, "\\$1"); // 两个反斜杠变一个
  
  // 处理常见的数学函数，确保它们有反斜杠前缀
  const mathFunctions = ['sin', 'cos', 'tan', 'cot', 'sec', 'csc', 'arcsin', 'arccos', 'arctan', 'ln', 'log', 'lim', 'int', 'sum', 'max', 'min', 'sup', 'inf', 'det', 'exp', 'arg'];
  mathFunctions.forEach(func => {
    const pattern = new RegExp(`(?<!\\\\)\\b${func}\\b(?!\\{)`, 'g');
    formatted = formatted.replace(pattern, `\\${func}`);
  });
  
  // 处理特殊符号
  const specialSymbols = [
    {symbol: 'infty', latex: '\\infty'},
    {symbol: 'pi', latex: '\\pi'},
    {symbol: 'alpha', latex: '\\alpha'},
    {symbol: 'beta', latex: '\\beta'},
    {symbol: 'gamma', latex: '\\gamma'},
    {symbol: 'delta', latex: '\\delta'},
    {symbol: 'theta', latex: '\\theta'},
    {symbol: 'lambda', latex: '\\lambda'},
    {symbol: 'sigma', latex: '\\sigma'},
    {symbol: 'omega', latex: '\\omega'},
    {symbol: 'mu', latex: '\\mu'},
    {symbol: 'nu', latex: '\\nu'},
    {symbol: 'rho', latex: '\\rho'},
    {symbol: 'epsilon', latex: '\\epsilon'},
    {symbol: 'partial', latex: '\\partial'},
    {symbol: 'nabla', latex: '\\nabla'},
    {symbol: 'pm', latex: '\\pm'},
    {symbol: 'mp', latex: '\\mp'},
    {symbol: 'times', latex: '\\times'},
    {symbol: 'div', latex: '\\div'},
    {symbol: 'cdot', latex: '\\cdot'}
  ];
  
  specialSymbols.forEach(({symbol, latex}) => {
    const pattern = new RegExp(`(?<!\\\\)\\b${symbol}\\b`, 'g');
    formatted = formatted.replace(pattern, latex);
  });
  
  // 修复极限表达式中的问题
  // 1. 首先处理明显错误的格式
  formatted = formatted.replace(/\\t\\to/g, '\\to'); // 修复 \t\to 错误
  
  // 2. 确保\lim后面有适当的格式
  formatted = formatted.replace(/\\lim([^_{\s])/g, "\\lim $1");
  
  // 3. 处理极限符号中的单独的o替换为\to（避免影响已有的\to）
  formatted = formatted.replace(/([a-zA-Z])\s+o\s+([a-zA-Z0-9+\-∞]+)(?!\s*\\)/g, "$1 \\to $2");
  
  // 4. 确保\lim_{x \to ...}格式正确
  formatted = formatted.replace(/\\lim_\{([^}]*)\s*(\\?to|->|o)\s*([^}]*)\}/g, "\\lim_{$1\\to $3}");
  
  // 5. 处理没有大括号的极限下标
  formatted = formatted.replace(/\\lim_([a-zA-Z])\s*(\\?to|->|o)\s*([a-zA-Z0-9+\-∞]+)/g, "\\lim_{$1\\to $3}");
  
  // 6. 处理特殊情况：lim_{x o 0}这种格式
  formatted = formatted.replace(/\\lim_\{([a-zA-Z0-9])\s*o\s*([a-zA-Z0-9+\-∞]+)\}/g, "\\lim_{$1\\to $2}");
  formatted = formatted.replace(/\\lim_([a-zA-Z0-9])\s*o\s*([a-zA-Z0-9+\-∞]+)/g, "\\lim_{$1\\to $2}");
  
  // 7. 确保无穷大符号正确
  formatted = formatted.replace(/infinity/g, '\\infty');
  formatted = formatted.replace(/∞/g, '\\infty');
  
  // 处理分数
  formatted = formatted.replace(/(?<!\\\\)frac\{([^}]*)\}\{([^}]*)\}/g, "\\frac{$1}{$2}");
  formatted = formatted.replace(/(?<!\\\\)frac\(([^,]*),([^)]*)\)/g, "\\frac{$1}{$2}");
  
  // 处理简单的分数形式 a/b - 修复版本
  formatted = formatted.replace(/([a-zA-Z0-9()+\-]+)\/([a-zA-Z0-9()+\-]+)/g, (match, p1, p2) => {
    // 避免修改已经是LaTeX格式的分数
    if (match.includes('\\frac')) return match;
    return `\\frac{${p1}}{${p2}}`;
  });
  
  // 处理下标和上标
  formatted = formatted.replace(/_([a-zA-Z0-9])(?![a-zA-Z0-9{}])/g, "_{$1}");
  formatted = formatted.replace(/\^([a-zA-Z0-9])(?![a-zA-Z0-9{}])/g, "^{$1}");
  
  // 处理根号
  formatted = formatted.replace(/(?<!\\\\)sqrt\(([^)]*)\)/g, "\\sqrt{$1}");
  formatted = formatted.replace(/(?<!\\\\)sqrt([^{\(])/g, "\\sqrt{$1}");
  
  // 处理积分符号
  formatted = formatted.replace(/\\int([^_^])/g, "\\int $1");
  formatted = formatted.replace(/dx([^_^})])/g, "\\,dx$1");
  
  // 处理矩阵中的换行符
  formatted = formatted.replace(/\\\\\\\\/g, "\\\\\\\\");
  
  return formatted;
};

// 自定义样式组件，覆盖KaTeX默认的错误显示样式
const StyledBlockMath = styled(BlockMath)(({ theme }) => ({
  '& .katex': {
    color: 'inherit', // 使用继承的颜色而不是默认的红色
    fontSize: '1.2em',
    fontFamily: '"KaTeX_Main", "Times New Roman", "SimSun", "宋体", serif',
  },
  '& .katex-error': {
    color: theme.palette.text.primary,
    backgroundColor: 'transparent',
    border: 'none',
    padding: 0,
  },
  '& .katex-display': {
    overflow: 'auto',
    padding: '10px 0',
    margin: '8px 0',
  },
  '& .katex-html': {
    fontFamily: '"KaTeX_Main", "Times New Roman", "SimSun", "宋体", serif',
  }
}));

const StyledInlineMath = styled(InlineMath)(({ theme }) => ({
  '& .katex': {
    color: 'inherit',
    fontSize: '1.1em',
    fontFamily: '"KaTeX_Main", "Times New Roman", "SimSun", "宋体", serif',
  },
  '& .katex-error': {
    color: theme.palette.text.primary,
    backgroundColor: 'transparent',
    border: 'none',
    padding: 0,
  },
  '& .katex-html': {
    fontFamily: '"KaTeX_Main", "Times New Roman", "SimSun", "宋体", serif',
  }
}));

const TestGenerator = () => {
  const [testConfig, setTestConfig] = useState({
    type: '',
    difficulty: '',
    questionCount: 10,
    topics: '',
    includeAnswers: true,
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [generatedTest, setGeneratedTest] = useState(null);
  const [openPreview, setOpenPreview] = useState(false);
  
  // 数学题类型选项
  const mathTypes = [
    { value: 'calculus', label: '微积分', topics: ['极限', '导数', '积分', '微分方程'] },
    { value: 'algebra', label: '线性代数', topics: ['矩阵', '行列式', '向量空间', '特征值'] },
    { value: 'probability', label: '概率统计', topics: ['概率分布', '随机变量', '假设检验', '回归分析'] },
  ];
  
  // 当前选择类型的可用主题
  const [availableTopics, setAvailableTopics] = useState([]);
  
  // 当类型改变时更新可用主题
  useEffect(() => {
    if (testConfig.type) {
      const selectedType = mathTypes.find(type => type.value === testConfig.type);
      if (selectedType) {
        setAvailableTopics(selectedType.topics);
      }
    } else {
      setAvailableTopics([]);
    }
  }, [testConfig.type]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTestConfig((prev) => ({
      ...prev,
      [name]: value,
    }));
  };
  
  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setTestConfig((prev) => ({
      ...prev,
      [name]: checked,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setGeneratedTest(null);
    
    try {
      // 构建请求参数
      const requestData = {
        ...testConfig,
        questionCount: parseInt(testConfig.questionCount, 10),
      };
      
      // 调用后端API生成测试题
      const response = await testsAPI.generateTest(requestData);
      
      if (response.data && response.data.questions) {
        setGeneratedTest(response.data);
      } else {
        throw new Error('生成测试题失败，请重试');
      }
    } catch (err) {
      console.error('生成测试题错误:', err);
      setError('生成测试题失败，请重试');
      
      // 如果后端API未实现，使用模拟数据进行演示
      simulateTestGeneration();
    } finally {
      setLoading(false);
    }
  };
  
  // 模拟测试生成（当后端API未实现时使用）
  const simulateTestGeneration = () => {
    // 根据选择的类型和难度生成模拟题目
    const mockQuestions = [];
    const difficultyMap = {
      easy: '简单',
      medium: '中等',
      hard: '困难'
    };
    
    const typeLabel = mathTypes.find(t => t.value === testConfig.type)?.label || '数学';
    
    // 使用OpenAI API模拟生成题目（实际项目中应在后端实现）
    // 这里仅作为前端演示
    for (let i = 1; i <= testConfig.questionCount; i++) {
      let questionText = '';
      
      // 根据不同类型生成不同题目
      if (testConfig.type === 'calculus') {
        if (testConfig.difficulty === 'easy') {
          questionText = `计算函数 f(x) = x^2 + ${i} 在点 x = ${i} 处的导数。`;
        } else if (testConfig.difficulty === 'medium') {
          questionText = `求函数 f(x) = x^3 - ${i+2}x^2 + ${i*2}x - ${i} 的极值点。\\求导数 f'(x) = 3x^2 - ${2*(i+2)}x + ${i*2}，令 f'(x) = 0，解得 x = \\frac{${i+2} \\pm \\sqrt{${(i+2)*(i+2)} - 3 \\cdot ${i*2}}}{3}`;
        } else {
          questionText = `计算积分 \int (x^2 + \sin(${i}x)) dx`;
        }
      } else if (testConfig.type === 'algebra') {
        if (testConfig.difficulty === 'easy') {
          questionText = `计算矩阵 A = \\begin{pmatrix} ${i} & ${i+1} \\\\ ${i+2} & ${i+3} \\end{pmatrix} 的行列式。`;
        } else if (testConfig.difficulty === 'medium') {
          questionText = `求矩阵 A = \\begin{pmatrix} ${i} & ${i+1} & ${i+2} \\\\ ${i+3} & ${i+4} & ${i+5} \\\\ ${i+6} & ${i+7} & ${i+8} \\end{pmatrix} 的特征值。`;
        } else {
          questionText = `证明向量组 \\{(${i},${i+1},${i+2}), (${i+3},${i+4},${i+5}), (${i+6},${i+7},${i+8})\\} 是线性相关的。`;
        }
      } else if (testConfig.type === 'probability') {
        if (testConfig.difficulty === 'easy') {
          questionText = `一个骰子投掷${i+1}次，求至少出现一次6点的概率。`;
        } else if (testConfig.difficulty === 'medium') {
          questionText = `设随机变量X的概率密度函数为f(x)=${i}e^{-${i}x}，x>0，求E(X)和D(X)。`;
        } else {
          questionText = `设总体X服从正态分布N(\\mu,\\sigma^2)，从该总体中抽取容量为${i+10}的样本，样本均值为${i+5}，样本方差为${i+2}，试构造\\mu的置信水平为0.95的置信区间。`;
        }
      } else {
        questionText = `第${i}题：这是一道${difficultyMap[testConfig.difficulty] || ''}难度的${typeLabel}题目。`;
      }
      
      // 生成答案（如果需要）
      let answer = '';
      if (testConfig.includeAnswers) {
        if (testConfig.type === 'calculus') {
          if (testConfig.difficulty === 'easy') {
            answer = `f'(${i}) = 2 \\cdot ${i} = ${2*i}`;
          } else if (testConfig.difficulty === 'medium') {
            answer = `令f'(x) = 0，得到x = ${i/3} 或 x = ${i}。\\代入二阶导数 f''(x) = 6x - ${2*(i+2)} 判断极值类型。`;
          } else {
            answer = `\\int (x^2 + \\sin(${i}x)) dx = \frac{x^3}{3} - \frac{\cos(${i}x)}{${i}} + C`;
          }
        } else if (testConfig.type === 'algebra') {
          if (testConfig.difficulty === 'easy') {
            answer = `\\det(A) = ${i} \\times ${i+3} - ${i+1} \\times ${i+2} = ${i*(i+3) - (i+1)*(i+2)}`;
          } else {
            answer = `特征值计算过程略，结果为\\lambda_1 = ${i+i}, \\lambda_2 = ${i+2}, \\lambda_3 = ${i+4}`;
          }
        } else if (testConfig.type === 'probability') {
          if (testConfig.difficulty === 'easy') {
            answer = `1 - (\frac{5}{6})^{${i+1}} \approx ${(1 - Math.pow(5/6, i+1)).toFixed(4)}`;
          } else if (testConfig.difficulty === 'medium') {
            answer = `E(X) = \frac{1}{${i}}，D(X) = \frac{1}{${i*i}}`;
          } else {
            answer = `\mu的95\%置信区间为 [${i+5} - 1.96 \cdot \frac{\sqrt{${i+2}}}{\sqrt{${i+10}}}, ${i+5} + 1.96 \cdot \frac{\sqrt{${i+2}}}{\sqrt{${i+10}}}]`;
          }
        } else {
          answer = `解答过程略。最终结果：${Math.random().toFixed(4)}`;
        }
      }
      
      mockQuestions.push({
        id: i,
        question: questionText,
        answer: answer,
        type: testConfig.type,
        difficulty: testConfig.difficulty,
        topic: availableTopics[i % availableTopics.length] || '通用'
      });
    }
    
    // 设置生成的测试
    setGeneratedTest({
      title: `${typeLabel}${difficultyMap[testConfig.difficulty] || ''}难度测试`,
      description: `包含${testConfig.questionCount}道${typeLabel}题目，难度级别：${difficultyMap[testConfig.difficulty] || '未指定'}`,
      createdAt: new Date().toISOString(),
      questions: mockQuestions
    });
  };

  // 处理数学公式格式化，确保正确渲染各种数学符号
  // Remove the formatMathExpression function from here since it's now defined outside
  
  const handlePrint = () => {
    const printWindow = window.open('', '_blank');
    
    if (!printWindow) {
      setError('打印窗口被阻止，请允许弹出窗口');
      return;
    }
    
    // 构建打印内容
    let printContent = `
      <html>
        <head>
          <title>${generatedTest.title}</title>
          <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/katex@0.16.22/dist/katex.min.css" integrity="sha384-3UiQGuEI4TTMaFmGIZumfRPtfKQ3trwQE2JgosJxCnGmQpL/lJdjpcHkaaFwHlcI" crossorigin="anonymous">
          <script defer src="https://cdn.jsdelivr.net/npm/katex@0.16.22/dist/katex.min.js" integrity="sha384-cpW21h6RZv/phavutF+AuVYrr+dA8xD9zs6FwLpaCct6O9ctzYFfFr4dgmgccOTx" crossorigin="anonymous"></script>
          <script defer src="https://cdn.jsdelivr.net/npm/katex@0.16.22/dist/contrib/auto-render.min.js" integrity="sha384-+XBljXPPiv+OzfbB3cVmLHf4hdUFHlWNZN5spNQ7rmHTXpd7WvJum6fIACpNNfIR" crossorigin="anonymous"></script>
          <script>
            document.addEventListener("DOMContentLoaded", function() {
              renderMathInElement(document.body, {
                delimiters: [
                  {left: "$$", right: "$$", display: true},
                  {left: "$", right: "$", display: false}
                ],
                throwOnError: false,
                errorColor: '#333',
                strict: false
              });
            });
          </script>
          <style>
            body { font-family: "Microsoft YaHei", "微软雅黑", "SimSun", "宋体", Arial, sans-serif; padding: 20px; }
            h1 { text-align: center; }
            .question { margin-bottom: 20px; }
            .answer { margin-top: 10px; margin-bottom: 30px; padding: 10px; background-color: #f5f5f5; }
            /* 自定义KaTeX样式 */
            .katex { font-size: 1.2em !important; color: #333 !important; }
            .katex-error { color: #333 !important; background-color: transparent !important; border: none !important; }
            .katex-display { overflow: auto; padding: 10px 0; margin: 8px 0; }
            .katex-html { font-family: "KaTeX_Main", "Times New Roman", "SimSun", "宋体", serif !important; }
            /* 中文样式 */
            p, h3, strong { font-family: "Microsoft YaHei", "微软雅黑", "SimSun", "宋体", Arial, sans-serif; }
            @media print {
              .no-print { display: none; }
              button { display: none; }
            }
          </style>
        </head>
        <body>
          <div class="no-print" style="text-align: right; margin-bottom: 20px;">
            <button onclick="window.print()">打印</button>
          </div>
          <h1>${generatedTest.title}</h1>
          <p>${generatedTest.description}</p>
          <hr>
    `;
    
    // 添加题目
    generatedTest.questions.forEach((q, index) => {
      // 根据题目类型决定是否使用KaTeX格式
      let questionContent = q.question;
      let answerContent = q.answer;
      
      if (testConfig.type === 'calculus' || testConfig.type === 'algebra' || testConfig.type === 'probability') {
        questionContent = `$$${formatMathExpression(q.question)}$$`;
        if (q.answer) {
          answerContent = `$$${formatMathExpression(q.answer)}$$`;
        }
      }
      
      printContent += `
        <div class="question">
          <h3>题目 ${index + 1}</h3>
          <p>${questionContent}</p>
      `;
      
      if (testConfig.includeAnswers && q.answer) {
        printContent += `
          <div class="answer">
            <strong>答案：</strong>
            <p>${answerContent}</p>
          </div>
        `;
      }
      
      printContent += `</div>`;
    });

    
    printContent += `
        </body>
      </html>
    `;
    
    printWindow.document.open();
    printWindow.document.write(printContent);
    printWindow.document.close();
  };
  
  // 处理保存功能
  const handleSave = async () => {
    try {
      const response = await testsAPI.saveTest(generatedTest);
      if (response.data && response.data.success) {
        alert('测试已成功保存！');
      } else {
        throw new Error('保存失败');
      }
    } catch (err) {
      console.error('保存测试失败:', err);
      setError('保存测试失败，请重试');
    }
  };
  
  // 预览对话框渲染函数
  const renderTestPreviewDialog = () => (
    <Dialog 
      open={openPreview} 
      onClose={() => setOpenPreview(false)}
      fullWidth
      maxWidth="md"
    >
      <DialogTitle>
        {generatedTest?.title}
        <IconButton
          aria-label="close"
          onClick={() => setOpenPreview(false)}
          sx={{ position: 'absolute', right: 8, top: 8 }}
        >
          <Close />
        </IconButton>
      </DialogTitle>
      <DialogContent dividers>
        {generatedTest?.questions.map((q, index) => (
          <Box key={index} sx={{ mb: 3 }}>
            <Typography variant="h6" gutterBottom>
              题目 {index + 1}
            </Typography>
            <Typography variant="body1" paragraph>
              {testConfig.type === 'calculus' || testConfig.type === 'algebra' || testConfig.type === 'probability' ? 
                <StyledBlockMath math={formatMathExpression(q.question)} errorColor="inherit" /> : 
                q.question
              }
            </Typography>
            {testConfig.includeAnswers && q.answer && (
              <Box sx={{ mt: 1, p: 2, bgcolor: '#f5f5f5', borderRadius: 1, overflow: 'auto', maxWidth: '100%' }}>
                <Typography variant="subtitle2" gutterBottom>
                  答案：
                </Typography>
                <Typography variant="body2" sx={{ overflowX: 'auto', overflowY: 'visible', minHeight: '50px' }}>
                  {testConfig.type === 'calculus' || testConfig.type === 'algebra' || testConfig.type === 'probability' ? 
                    <StyledBlockMath math={formatMathExpression(q.answer)} errorColor="inherit" /> : 
                    q.answer
                  }
                </Typography>
              </Box>
            )}
            <Divider sx={{ mt: 2 }} />
          </Box>
        ))}
      </DialogContent>
      <DialogActions>
        <Button onClick={handlePrint} startIcon={<Print />}>
          打印
        </Button>
        <Button onClick={handleSave} startIcon={<Save />} color="primary">
          保存
        </Button>
      </DialogActions>
    </Dialog>
  );

  return (
    <Container maxWidth="md">
      {error && (
        <Alert severity="error" sx={{ mt: 2, mb: 2 }}>
          {error}
        </Alert>
      )}
      
      <Paper elevation={3} sx={{ p: 4, mt: 4 }}>
        <Typography variant="h4" align="center" gutterBottom>
          生成测试
        </Typography>
        <Typography variant="body1" paragraph align="center" color="text.secondary">
          配置测试参数，生成个性化数学题目测试
        </Typography>
        
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 3 }}>
          <Grid container spacing={3}>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth>
                <InputLabel>测试类型</InputLabel>
                <Select
                  name="type"
                  value={testConfig.type}
                  label="测试类型"
                  onChange={handleChange}
                  required
                >
                  {mathTypes.map((type) => (
                    <MenuItem key={type.value} value={type.value}>{type.label}</MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth>
                <InputLabel>难度等级</InputLabel>
                <Select
                  name="difficulty"
                  value={testConfig.difficulty}
                  label="难度等级"
                  onChange={handleChange}
                  required
                >
                  <MenuItem value="easy">简单</MenuItem>
                  <MenuItem value="medium">中等</MenuItem>
                  <MenuItem value="hard">困难</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="题目数量"
                name="questionCount"
                type="number"
                value={testConfig.questionCount}
                onChange={handleChange}
                InputProps={{ inputProps: { min: 1, max: 50 } }}
                required
              />
            </Grid>
            
            {testConfig.type && availableTopics.length > 0 && (
              <Grid item xs={12}>
                <FormControl fullWidth>
                  <InputLabel>知识点</InputLabel>
                  <Select
                    name="topics"
                    value={testConfig.topics}
                    label="知识点"
                    onChange={handleChange}
                  >
                    <MenuItem value="">全部</MenuItem>
                    {availableTopics.map((topic) => (
                      <MenuItem key={topic} value={topic}>{topic}</MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
            )}
            
            <Grid item xs={12}>
              <FormControl component="fieldset" sx={{ mt: 2 }}>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  其他选项
                </Typography>
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <input
                    type="checkbox"
                    id="includeAnswers"
                    name="includeAnswers"
                    checked={testConfig.includeAnswers}
                    onChange={handleCheckboxChange}
                  />
                  <label htmlFor="includeAnswers" style={{ marginLeft: '8px' }}>
                    包含答案
                  </label>
                </Box>
              </FormControl>
            </Grid>
            
            <Grid item xs={12}>
              <Button
                type="submit"
                fullWidth
                variant="contained"
                size="large"
                sx={{ mt: 2 }}
                disabled={loading}
              >
                {loading ? <CircularProgress size={24} /> : '生成测试'}
              </Button>
            </Grid>
          </Grid>
        </Box>
      </Paper>
      
      {generatedTest && (
        <Paper elevation={3} sx={{ p: 4, mt: 4 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
            <Typography variant="h5">
              生成的测试
            </Typography>
            <Box>
              <Button 
                variant="outlined" 
                startIcon={<Print />} 
                onClick={handlePrint}
                sx={{ mr: 1 }}
              >
                打印
              </Button>
              <Button 
                variant="outlined" 
                startIcon={<Save />} 
                onClick={handleSave}
              >
                保存
              </Button>
            </Box>
          </Box>
          
          <Typography variant="body1" paragraph>
            {generatedTest.description}
          </Typography>
          
          <Box sx={{ mb: 3 }}>
            <Grid container spacing={2}>
              {generatedTest.questions.slice(0, 3).map((question, index) => (
                <Grid item xs={12} key={index}>
                  <Card variant="outlined">
                    <CardContent>
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                        <Typography variant="subtitle1" gutterBottom>
                          题目 {index + 1}
                        </Typography>
                        <Chip 
                          label={question.topic} 
                          size="small" 
                          color="primary" 
                          variant="outlined" 
                        />
                      </Box>
                      <Typography variant="body1" paragraph>
                        {testConfig.type === 'calculus' || testConfig.type === 'algebra' || testConfig.type === 'probability' ? 
                          <StyledBlockMath math={formatMathExpression(question.question)} errorColor="inherit" /> : 
                          question.question
                        }
                      </Typography>
                      {testConfig.includeAnswers && question.answer && (
                        <Box sx={{ mt: 1, p: 2, bgcolor: '#f5f5f5', borderRadius: 1, overflow: 'auto', maxWidth: '100%' }}>
                          <Typography variant="subtitle2" gutterBottom>
                            答案：
                          </Typography>
                          <Typography variant="body2" sx={{ overflowX: 'auto', overflowY: 'visible', minHeight: '50px' }}>
                            {testConfig.type === 'calculus' || testConfig.type === 'algebra' || testConfig.type === 'probability' ? 
                              <StyledBlockMath math={formatMathExpression(question.answer)} errorColor="inherit" /> : 
                              question.answer
                            }
                          </Typography>
                        </Box>
                      )}
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Box>
          
          {generatedTest.questions.length > 3 && (
            <Box sx={{ textAlign: 'center', mt: 2 }}>
              <Button 
                variant="outlined" 
                onClick={() => setOpenPreview(true)}
                startIcon={<Refresh />}
              >
                查看全部题目
              </Button>
            </Box>
          )}
          
          {renderTestPreviewDialog()}
        </Paper>
      )}
    </Container>
  );
};

export default TestGenerator;

// 添加 MathJax 图片渲染功能
const renderMathAsImage = async (mathExpression) => {
  try {
    // 使用 MathJax 的在线 API 生成数学公式图片
    const encodedMath = encodeURIComponent(mathExpression);
    const imageUrl = `https://math.vercel.app/?from=${encodedMath}`;
    return imageUrl;
  } catch (error) {
    console.error('数学公式图片渲染失败:', error);
    return null;
  }
};

// 修改题目渲染组件
const MathDisplay = ({ expression, fallbackToImage = true }) => {
  const [imageUrl, setImageUrl] = useState(null);
  const [showImage, setShowImage] = useState(false);
  
  const handleKaTeXError = async () => {
    if (fallbackToImage && !showImage) {
      const url = await renderMathAsImage(expression);
      if (url) {
        setImageUrl(url);
        setShowImage(true);
      }
    }
  };
  
  if (showImage && imageUrl) {
    return (
      <img 
        src={imageUrl} 
        alt={expression}
        style={{ maxWidth: '100%', height: 'auto' }}
        onError={() => setShowImage(false)}
      />
    );
  }
  
  return (
    <StyledBlockMath 
      math={formatMathExpression(expression)}
      errorColor="#333"
      renderError={(error) => {
        handleKaTeXError();
        return <span style={{color: '#333'}}>{expression}</span>;
      }}
    />
  );
};
